mapper.select:
select id as id, title as title, method as method, business_type as businessType, request_method as requestMethod, operate_name as operateName, operate_url as operateUrl, operate_address as operateAddress, request_param as requestParam, response_body as responseBody, success as success, error_msg as errorMsg, create_time as createTime, description as description, request_body as requestBody, logging_type as loggingType, browser as browser, system_os as systemOs from sys_logging where logging_type = #{loggingType}
and create_time >= #{startTime}
and create_time <= #{endTime}
order by create_time desc
