mapper.update:
update nb_article a
set a.top = a.top - 1
where a.top > #{currentTop}

UPDATE nb_article a
SET a.views = a.views + 1
WHERE a.id = #{articleId}

update nb_article
set approve_cnt = approve_cnt + 1
where id = #{articleId}

mapper.select:
select * from nb_article a where a.draft != 1
and a.title like concat('%',#{title},'%')
and a.id in (select rac.article_id from refer_article_cate rac where rac.cate_id in
#{item}
)

select *
from nb_article a
where a.draft !=1

    and a.title like concat('%',#{title},'%')

and a.id not in(
select rac.article_id from refer_article_cate rac where rac.cate_id = #{productCateId}
);
